INSERT INTO `templates` VALUES (744, NULL, 626, NULL, 'PortalService.java.btl', '2', 'fund-core-dao/src/main/java/cn/com/ehomepay/fund/dao/service/portal/', '0', 'cn.com.ehomepay.fund.dao.service.portal', NULL, '', 'package ${packageInfo};\n\nimport ${basePackage}.dao.service.portal.dtvo.dto.${entity}QueryDTO;\nimport ${basePackage}.dao.service.portal.dtvo.dto.${entity}UpdateDTO;\nimport ${basePackage}.dao.service.portal.dtvo.vo.${entity}VO;\nimport ${basePackage}.dao.entity.${entity};\nimport ${basePackage}.utils.PageVo;\nimport com.baomidou.mybatisplus.extension.service.IService;\n\n/**\n * @description portal页面接口类\n * @Author zhanglei451\n * @Date 2022/1/5\n */\npublic interface ${entity}PortalService extends IService<${entity}> {\n\n\n    ${entity}VO editHistory(Long id);\n\n    PageVo<${entity}VO> queryPage(${entity}QueryDTO tBusinessCodeQueryDTO);\n\n    ${entity}VO add(${entity}UpdateDTO ${entityNameLowerCamelCase}UpdateDTO);\n\n    ${entity}VO edit(${entity}UpdateDTO ${entityNameLowerCamelCase}UpdateDTO);\n}\n', NULL, '', '2022-01-05 15:09:20', 'zhanglei001', 9, 'bk_2509711c8b554d98832d1e1a39151df1', '2022-01-05 17:22:55', 'zhanglei001', 'bk_2509711c8b554d98832d1e1a39151df1', NULL, 3, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `templates` VALUES (745, NULL, 626, NULL, 'PortalServiceImpl.java.btl', '2', 'fund-core-dao/src/main/java/cn/com/ehomepay/fund/dao/service/portal/impl', '0', 'cn.com.ehomepay.fund.dao.service.portal.impl', NULL, '', 'package ${packageInfo};\n\nimport ${basePackage}.dao.service.portal.dtvo.dto.${entity}QueryDTO;\nimport ${basePackage}.dao.service.portal.dtvo.dto.${entity}UpdateDTO;\nimport ${basePackage}.dao.service.portal.dtvo.vo.${entity}VO;\nimport ${basePackage}.dao.entity.${entity};\nimport ${basePackage}.dao.mapper.${entity}Mapper;\nimport ${basePackage}.dao.service.portal.${entity}PortalService;\nimport ${basePackage}.enums.ResultCode;\nimport ${basePackage}.exception.ServiceException;\nimport ${basePackage}.utils.PageVo;\nimport ${basePackage}.utils.ResultVo;\nimport com.baomidou.mybatisplus.core.conditions.query.QueryWrapper;\nimport com.baomidou.mybatisplus.extension.service.impl.ServiceImpl;\nimport com.github.pagehelper.PageHelper;\nimport com.github.pagehelper.PageInfo;\nimport org.springframework.beans.BeanUtils;\nimport org.springframework.stereotype.Service;\nimport org.springframework.util.StringUtils;\n\nimport java.util.ArrayList;\nimport java.util.List;\n\n/**\n * <p>\n * ${table.comment!}\n * </p>\n *\n * @author ${author}\n * @since ${date}\n */\n@Service\npublic class ${entity}PortalServiceImpl extends ServiceImpl<${entity}Mapper, ${entity}> implements ${entity}PortalService {\n\n    /**\n     * @description 查询 - 单条\n     * @param id\n     * @author ${author}\n     * @Date ${date}\n     */\n    @Override\n    public ${entity}VO editHistory(Long id) {\n        QueryWrapper<${entity}> queryWrapper = new QueryWrapper<>();\n        queryWrapper.eq(\"id\",id);\n        ${entity} one = super.getOne(queryWrapper);\n        ${entity}VO ${entityNameLowerCamelCase} = new ${entity}VO();\n        if(one != null){\n            BeanUtils.copyProperties(one,${entityNameLowerCamelCase});\n        }\n        return ${entityNameLowerCamelCase};\n    }\n\n    /**\n     * @description 查询 - 分页\n     * @author ${author}\n     * @Date ${date}\n     */\n    @Override\n    public PageVo<${entity}VO> queryPage(${entity}QueryDTO ${entityNameLowerCamelCase}QueryDTO) {\n        QueryWrapper<${entity}> queryWrapper = new QueryWrapper<>();\n\n        // 需要补充查询条件代码 TODO \n        queryWrapper.orderByDesc(\"id\");\n        // 开启分页\n        PageHelper.startPage(${entityNameLowerCamelCase}QueryDTO.getPageNo(), ${entityNameLowerCamelCase}QueryDTO.getPageSize());\n        List<${entity}> list = super.list(queryWrapper);\n        PageInfo<${entity}> pageInfo = new PageInfo<>(list);\n        // 分页\n        List<${entity}VO> queryVOList = new ArrayList<>();\n        for (${entity} ${entityNameLowerCamelCase}DB : list) {\n            ${entity}VO ${entityNameLowerCamelCase}VO = new ${entity}VO();\n            BeanUtils.copyProperties(${entityNameLowerCamelCase}DB, ${entityNameLowerCamelCase}VO);\n            queryVOList.add(${entityNameLowerCamelCase}VO);\n        }\n        return new PageVo<>(queryVOList,pageInfo.getPageNum(),pageInfo.getPageSize(),pageInfo.getTotal());\n    }\n\n    /**\n     * @description 新增\n     * @author ${author}\n     * @Date ${date}\n     */\n    @Override\n    public ${entity}VO add(${entity}UpdateDTO ${entityNameLowerCamelCase}UpdateDTO) {\n        ${entity} ${entityNameLowerCamelCase} = new ${entity}();\n        BeanUtils.copyProperties(${entityNameLowerCamelCase}UpdateDTO,${entityNameLowerCamelCase});\n        super.save(${entityNameLowerCamelCase});\n        ${entity}VO ${entityNameLowerCamelCase}VO = new ${entity}VO();\n        BeanUtils.copyProperties(${entityNameLowerCamelCase},${entityNameLowerCamelCase}VO);\n        return ${entityNameLowerCamelCase}VO;\n    }\n\n    /**\n     * @description 更新\n     * @author ${author}\n     * @Date ${date}\n     */\n    @Override\n    public ${entity}VO edit(${entity}UpdateDTO ${entityNameLowerCamelCase}UpdateDTO) {\n        ${entity} ${entityNameLowerCamelCase} = new ${entity}();\n        BeanUtils.copyProperties(${entityNameLowerCamelCase}UpdateDTO,${entityNameLowerCamelCase});\n        boolean b = super.updateById(${entityNameLowerCamelCase});\n        if(b){\n            ${entity} byId = super.getById(${entityNameLowerCamelCase}UpdateDTO.getId());\n            ${entity}VO ${entityNameLowerCamelCase}VO = new ${entity}VO();\n            BeanUtils.copyProperties(byId,${entityNameLowerCamelCase}VO);\n            return ${entityNameLowerCamelCase}VO;\n        }\n        throw new ServiceException(new ResultVo(ResultCode.ERROR.getCode() , \"更新失败!\"));\n    }\n}\n', NULL, '', '2022-01-05 15:19:59', 'zhanglei001', 9, 'bk_2509711c8b554d98832d1e1a39151df1', '2022-01-05 17:22:37', 'zhanglei001', 'bk_2509711c8b554d98832d1e1a39151df1', NULL, 3, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `templates` VALUES (746, NULL, 626, NULL, 'Controller.java.btl', '2', 'fund-core-center/src/main/java/cn/com/ehomepay/fund/controller/portal/v1/', '0', 'cn.com.ehomepay.fund.controller.portal.v1', NULL, '', 'package ${packageInfo};\n\nimport ${basePackage}.aop.WebLog;\nimport ${basePackage}.base.BaseController;\nimport ${basePackage}.dao.service.portal.dtvo.dto.${entity}QueryDTO;\nimport ${basePackage}.dao.service.portal.dtvo.dto.${entity}UpdateDTO;\nimport ${basePackage}.dao.service.portal.dtvo.vo.${entity}VO;\nimport ${basePackage}.dao.service.portal.${entity}PortalService;\nimport ${basePackage}.utils.PageVo;\nimport ${basePackage}.utils.ResultVo;\nimport io.swagger.annotations.Api;\nimport io.swagger.annotations.ApiOperation;\nimport lombok.extern.slf4j.Slf4j;\nimport org.springframework.beans.factory.annotation.Autowired;\nimport org.springframework.http.MediaType;\nimport org.springframework.web.bind.annotation.*;\n\n@RestController\n@RequestMapping(\"/portal/v1/${entityNameLowerCamelCase}\")\n@Api(tags = {\"${table.comment!} web接口\"})\n@Slf4j\npublic class ${entity}Controller  extends BaseController {\n\n    @Autowired\n    ${entity}PortalService ${entityNameLowerCamelCase}Service;\n\n    @WebLog\n    @ApiOperation(value = \"查询 - 单条\")\n    @GetMapping(value = \"/editHistory\")\n    public ResultVo<${entity}VO> editHistory(@RequestParam(name = \"id\") Long id) {\n        return success(${entityNameLowerCamelCase}Service.editHistory(id));\n    }\n\n    @WebLog(description = \"查询 - 分页\")\n    @PostMapping(value = \"/queryPage\", consumes = { MediaType.APPLICATION_JSON_UTF8_VALUE })\n    public ResultVo<PageVo<${entity}VO>> queryPage(@RequestBody ${entity}QueryDTO ${entityNameLowerCamelCase}QueryDTO) {\n        return success(${entityNameLowerCamelCase}Service.queryPage(${entityNameLowerCamelCase}QueryDTO));\n    }\n\n    @WebLog(description = \"新增\")\n    @PostMapping(value = \"/add\", consumes = { MediaType.APPLICATION_JSON_UTF8_VALUE })\n    public ResultVo<${entity}VO> add(@RequestBody ${entity}UpdateDTO ${entityNameLowerCamelCase}UpdateDTO) {\n        return success(${entityNameLowerCamelCase}Service.add(${entityNameLowerCamelCase}UpdateDTO));\n    }\n\n    @WebLog(description = \"修改\")\n    @PostMapping(value = \"/edit\", consumes = { MediaType.APPLICATION_JSON_UTF8_VALUE })\n    public ResultVo<${entity}VO> edit(@RequestBody ${entity}UpdateDTO ${entityNameLowerCamelCase}UpdateDTO) {\n        return success(${entityNameLowerCamelCase}Service.edit(${entityNameLowerCamelCase}UpdateDTO));\n    }\n\n}', NULL, '', '2022-01-05 15:25:01', 'zhanglei001', 9, 'bk_2509711c8b554d98832d1e1a39151df1', '2022-01-05 17:21:37', 'zhanglei001', 'bk_2509711c8b554d98832d1e1a39151df1', NULL, 3, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `templates` VALUES (747, NULL, 626, NULL, 'boss-modules.yaml.btl', '0', 'fundplatform/', '0', '', NULL, '', '#模块code\nmodule: fundplatform\n#模板中文名称用于菜单显示和权限分配\nlabel: 资金服务运营平台\nisNew: false\ndescription: 资金服务运营平台\n#字典清单\n#dictionarys:\n#  - code: shareConfigType\n#    label: 配置方式\n#    items:\n#      - {code: 0,label: 非自动 }\n#      - {code: 1,label: 自动 }\n#  - code: templateType\n#    label: 账号类型\n#    items:\n#      - {code: MERCHANT,label: 收款商户号 }\n#      - {code: PERSON,label: 会员号 }\n#菜单清单\nmenus:\n  #以下item都指item这个值 item 命名规则 module/具体子模块名\n  - item: \"fundplatform/${entityNameLowerCamelCase}\"\n    label: ${table.comment!}管理\n    description: ${table.comment!}管理\n    # 当有page配置项时 url必须为/feboss/{item}\n    url: \"/feboss/fundplatform/${entityNameLowerCamelCase}\"\n    # 对应的功能点，包括列表\n    functions:\n      - code: \"fundplatform/${entityNameLowerCamelCase}/edit\"\n        label: ${table.comment!}编辑\n        apis:\n          - {portalUrl: \"fundplatform/${entityNameLowerCamelCase}Edit\",label: \"编辑\",host: \"http://fund-platform-core/\",url: \"portal/v1/${entityNameLowerCamelCase}/edit\",out: [],in: [],restful: false }\n          - {portalUrl: \"fundplatform/${entityNameLowerCamelCase}EditHistory\",label: \"编辑回显\",host: \"http://fund-platform-core/\",url: \"portal/v1/${entityNameLowerCamelCase}/editHistory\",out: [],in: [],restful: false }\n        page:\n          info: {uri: \"/fundplatform/${entityNameLowerCamelCase}Edit\",pageType: formPage,httpMethod: get,contentType: \'\',hasPreview: false,autoName: false}\n          areas:\n            #每个区域的标题\n            - title: \'\'\n              #区域的字段列表\n              columns:\n                #title 字段标题\n                #field 字段对应的参数名称\n                #type 字段类型项。money|string|dateTime|date|#[字典类型名称]|${dollar}[搜索帮助名称]\n                #display 显示区域。table-仅显示在表格区域, search-仅作为查询条件出现,both-都展现，readonly表示此项只读，默认取值both formPage可以不填写\n                #required 是否必填。默认false\n                #style   当type为字典和搜索帮助时 取值default(不填就是这个）表示前端显示为下拉框、single表示前端显示为单选按钮、multi表示前端显示为多选按钮\n                <% for(field in table.fields){ %>\n                - { title: ${field.comment}, field: ${field.propertyName},type: \"string\",display: table,required: true }\n                <% } %>\n      # code命名应遵循  查询功能值为 item；新增为item/add； 编辑为item/edit；删除为item/delete；详情 为 item/detail; 自定义按钮为item/customButtons;自定义行按钮为item/customRowButtons\n      - code: \"fundplatform/${entityNameLowerCamelCase}\"\n        label: ${table.comment!}配置查询\n        apis:\n          #portalUrl值为item label为接口中文名称，host 为 后台服务host地址，url 后台服务接口地址，out 出参转换规则 没有就写空数组，in入参转换规则 没有为空数组，restful 后台服务是否为restful接口。\n          - {portalUrl: \"fundplatform/${entityNameLowerCamelCase}\",label: \"\",host: \"http://fund-platform-core/\",url: \"portal/v1/${entityNameLowerCamelCase}/queryPage\",out:[],in:[],restful: false }\n        page:\n          #uri为前端模版的唯一标识 查询模版uri=/{item}\n          #pageType 页面类型，现有的取值：queryPage 查询页面、detailPage 详情页面、formPage为表单页面（包括新增和编辑）\n          #httpMethod http接口请求方式，取值 get/post\n          #contentType 请求header设置  取值 \'\'/application/json\n          #hasPreview 是否有预览界面，默认false\n          #autoName 是否自动生成boss的code和name,默认false\n          info: {uri: \"/fundplatform/${entityNameLowerCamelCase}\",pageType: queryPage,httpMethod: post,contentType: \'application/json\',hasPreview: false,autoName: false}\n          # processkey 工作流关键值 工作流用于区分不同的工作流 必传\n          #”form“审核预览展示模式简单固定可以看为提交表单不可编辑状态时的样式；”view“自定义审核预览模式。当工作流类型为view时需要联系BOSS前端研发添加审核预览模块\n          #一个页面允许多个区域，一个区域有 title-标题,code-区域标识和columns-字段列表构成\n          #其中只有detailPage的模式下code必须要有，否则数据无法正常展示\n          areas:\n            #每个区域的标题\n            - title: \'\'\n              #区域的字段列表\n              columns:\n                #title 字段标题\n                #field 字段对应的参数名称\n                #type 字段类型项。money|string|dateTime|date|#[字典类型名称]|${dollar}[搜索帮助名称]，若type为自己定义的字典，type为自定义字典名，如：type:\"testChannelStatus\"\n                #display 显示区域。table-仅显示在表格区域, search-仅作为查询条件出现,both-都展现，readonly表示此项只读，默认取值both，formPage中可以不填写\n                #required 是否必填。默认false\n                #style   当type为字典和搜索帮助时 取值default(不填就是这个）表示前端显示为下拉框、single表示前端显示为单选按钮、multi表示前端显示为多选按钮\n                <% for(field in table.fields){ %>\n                - { title: ${field.comment}, field: ${field.propertyName},type: \"string\",display: both }\n                <% } %>\n      - code: \"fundplatform/${entityNameLowerCamelCase}/add\"\n        label: 新增${table.comment!}配置\n        apis:\n          #工作流形式view时需要调用服务端自己的接口需要配置工作流； 一般情况下增删改等动作走工作流形式form 无需配置portal\n          #portalUrl值为{item}Add label为接口中文名称，host 为 后台服务host地址，url 后台服务接口地址，out 出参转换规则 没有就写空数组，in入参转换规则 没有为空数组，restful 后台服务是否为restful接口。\n          - {portalUrl: \"fundplatform/${entityNameLowerCamelCase}Add\",label: \"新增\",host: \"http://fund-platform-core/\",url: \"portal/v1/${entityNameLowerCamelCase}/add\",out: [],in: [],restful: true }\n        page:\n          #uri为前端模版的唯一标识 新增模版uri=/{item}Add (旧版中新增模版为uri=/{item}AddFormTp，配置之前可以问下黄拯是否支持新版的新增模版)\n          #pageType 页面类型，现有的取值：queryPage 查询页面、detailPage 详情页面、formPage为表单页面（包括新增和编辑）\n          #httpMethod http接口请求方式，取值 get/post\n          #contentType 请求header设置  取值 \'\'/application/json\n          #hasPreview 是否有预览界面，默认false\n          #autoName 是否自动生成boss的code和name,默认false\n          info: {uri: \"/fundplatform/${entityNameLowerCamelCase}Add\",pageType: formPage,httpMethod: post,contentType: \'application/json\',hasPreview: false,autoName: false}\n          # processkey 工作流关键值 工作流用于区分不同的工作流 必传\n          #”form“审核预览展示模式简单固定可以看为提交表单不可编辑状态时的样式；”view“自定义审核预览模式。当工作流类型为view时需要联系BOSS前端研发添加审核预览模块\n          workflow: {processkey: AccountConfigAdd,workflowType: form,businessKey: [channelType,payProduct,tag],operate: add}\n          #一个页面允许多个区域，一个区域有 title-标题,code-区域标识和columns-字段列表构成\n          #其中只有detailPage的模式下code必须要有，否则数据无法正常展示\n          areas:\n            #每个区域的标题\n            - title: \'\'\n              #区域的字段列表\n              columns:\n                #title 字段标题\n                #field 字段对应的参数名称\n                #type 字段类型项。money|string|dateTime|date|#[字典类型名称]|${dollar}[搜索帮助名称]\n                #display 显示区域。table-仅显示在表格区域, search-仅作为查询条件出现,both-都展现，readonly表示此项只读，默认取值both，formPage中可以不填写\n                #required 是否必填。默认false\n                #style   当type为字典和搜索帮助时 取值default(不填就是这个）表示前端显示为下拉框、single表示前端显示为单选按钮、multi表示前端显示为多选按钮\n                <% for(field in table.fields){ %>\n                - { title: ${field.comment}, field: ${field.propertyName},type: \"string\",required: true }\n                <% } %>\n', NULL, '', '2022-01-05 16:11:46', 'zhanglei001', 9, 'bk_2509711c8b554d98832d1e1a39151df1', '2022-01-06 09:57:30', 'zhanglei001', 'bk_2509711c8b554d98832d1e1a39151df1', NULL, 3, NULL, NULL, NULL, NULL, NULL);
