INSERT INTO `templates` VALUES (757, NULL, 627, NULL, 'Enums.java.btl', '2', 'permission-center-dtvo/src/main/java/top/buukle/permission/center/dtvo/enums/', '0', 'top.buukle.permission.center.dtvo.enums', NULL, '', 'package ${packageInfo};\n\n\n/**\n * @Author: elvin\n * @Date: 2019/7/28/028 3:56\n */\npublic class ${entity}Enums {\n\n    public enum status {\n\n        DELETED(-1,\"已经删除\"),\n        INIT(0,\"创建完成\"),\n        AUDITING(1,\"审核中\"),\n        REJECT(2,\"停用\"),\n        PUBLISHED(3,\"正常\"),\n        ;\n\n        private Integer status;\n        private String description;\n\n        status(int status, String description) {\n            this.description = description;\n            this.status = status;\n        }\n        public String getDescription() {\n            return description;\n        }\n        public Integer value() {\n            return status;\n        }\n\n        public static String getPairs() {\n            ${entity}Enums.status[] values = ${entity}Enums.status.values();\n            StringBuffer stringBuffer = new StringBuffer();\n            for (${entity}Enums.status status : values) {\n                stringBuffer.append(status.value());\n                stringBuffer.append(\";\");\n                stringBuffer.append(status.getDescription());\n                stringBuffer.append(\",\");\n            }\n            return stringBuffer.toString();\n        }\n    }\n\n    public enum auditStatus {\n\n        INIT(-1,\"待提交\"),\n        COMMITTED(0,\"提交审核\"),\n        AUDITING(1,\"审核中\"),\n        PUBLISHED(2,\"通过\"),\n        REJECT(3,\"不通过\"),\n        ;\n\n        private Integer status;\n        private String description;\n\n        auditStatus(int status, String description) {\n            this.description = description;\n            this.status = status;\n        }\n        public String getDescription() {\n            return description;\n        }\n        public Integer value() {\n            return status;\n        }\n\n  