 this.${dollar}refs.table.pagination.pageNo = 1\n      this.${dollar}refs.table.refresh(true)\n    },\n    handleAdd () {\n      this.initvalue = null\n      this.${dollar}refs.AddOrEditForm.show()\n    },\n    handleDelete (id) {\n      var obj = this.${dollar}refs.table\n      this.commonRequest.head.operationTime = Date.now()\n      this.bodyById.id = id\n      this.commonRequest.body = this.bodyById\n      const commonRequest = this.commonRequest\n      this.${dollar}confirm({\n        title: \'确认提示\',\n        content: `是否删除该条记录？`,\n        okType: \'danger\',\n        okText: \'确认\',\n        cancelText: \'取消\',\n        onOk () {\n          request({\n            url: \'/${entityNameLowerCamelCase}/deleteById\',\n            method: \'post\',\n            dataType: \'json\',\n            data: commonRequest\n          }).then(res => {\n            if (res.head.status === \'S\') {\n              message.success(\'成功!\')\n              obj.refresh()\n            } else {\n              message.error(res.head.msg)\n            }\n          })\n        }\n      })\n    },\n    handleEdit (id) {\n      this.commonRequest.head.operationTime = Date.now()\n      this.bodyById.id = id\n      this.commonRequest.body = this.bodyById\n      const commonRequest = this.commonRequest\n      request({\n        url: \'/${entityNameLowerCamelCase}/getById\',\n        method: \'post\',\n        dataType: \'json\',\n        data: commonRequest\n      }).then(res => {\n        if (res.head.status === \'S\') {\n          this.initvalue = res.body\n          this.${dollar}refs.AddOrEditForm.show()\n        } else {\n          message.error(res.head.msg)\n        }\n      }).catch((err) => {\n        console.log(err)\n      })\n    },\n    handleInfo (id) {\n      this.commonRequest.head.operationTime = Date.now()\n      this.bodyById.id = id\n      this.commonRequest.body = this.bodyById\n      const commonRequest = this.commonRequest\n      request({\n        url: \'/${entityNameLowerCamelCase}/getById\',\n        method: \'post\',\n        dataType: \'json\',\n        data: commonRequest\n      }).then(res => {\n        if (res.head.status === \'S\') {\n          this.initvalueInfo = res.body\n          this.${dollar}refs.InfoTable.show()\n        } else {\n          message.error(res.head.msg)\n        }\n      }).catch((err) => {\n        console.log(err)\n      })\n    },\n    handleBan (record) {\n      if (record.status !== 0) {\n        this.${dollar}message.info(`${dollar}{record.name} 已停用`)\n      } else {\n        this.${dollar}message.error(`${dollar}{record.name} 停用失败`)\n      }\n    },\n    onSelectChange (selectedRowKeys, selectedRows) {\n      this.selectedRowKeys = selectedRowKeys\n      this.selectedRows = selectedRows\n    },\n    toggleAdvanced () {\n      this.advanced = !this.advanced\n    },\n    gmtDateFormat (text) {\n      return moment(text).format(\'yyyy-MM-DD HH:mm:ss\')\n    }\n  },\n  data () {\n    this.columns = columns\n    return {\n      initvalue: {},\n      initvalueInfo: {},\n      // create model\n      visible: false,\n      confirmLoading: false,\n      dialogFormVisible: false,\n      mdl: null,\n      // 高级搜索 展开/关闭\n      advanced: false,\n      // 查询参数\n      queryParam: {},\n      commonRequest: {\n        head: {\n          operationTime: Date.now(),\n          appId: process.env.VUE_APP_BUUKLE_APP_ID\n        },\n        body: {}\n      },\n      bodyById: {\n        id: 0\n      },\n      // 加载数据方法 必须为 Promise 对象\n      loadData: parameter => {\n        const param = Object.assign({}, parameter, this.queryParam)\n        this.commonRequest.head.operationTime = Date.now()\n        this.commonRequest.body = param\n        const commonRequest = this.commonRequest\n        return request({\n          url: \'/${entityNameLowerCamelCase}/getPage\',\n          method: \'post\',\n          dataType: \'json\',\n          data: commonRequest\n        }).then(res => {\n          return res.result\n        })\n      },\n      selectedRowKeys: [],\n      selectedRows: []\n    }\n  },\n  filters: {\n    statusFilter (type) {\n      return statusMap[type].text\n    },\n    statusTypeFilter (type) {\n      return statusMap[type].status\n    }\n  },\n  created () {\n  },\n  computed: {\n    rowSelection () {\n      return {\n        selectedRowKeys: this.selectedRowKeys,\n        onChange: this.onSelectChange\n      }\n    }\n  }\n}\nconst columns = [\n  {\n    title: \'序号\',\n    width: \'5%\',\n    scopedSlots: { customRender: \'serial\' }\n  },\n  {\n    title: \'状态\',\n    dataIndex: \'status\',\n    width: \'5%\',\n    scopedSlots: { customRender: \'status\' }\n  },\n  {\n    title: \'操作\',\n    dataIndex: \'action\',\n    width: \'30%\',\n    scopedSlots: { customRender: \'action\' }\n  }\n]\nconst statusMap = {\n  1: {\n    status: \'default\',\n    text: \'创建完成\'\n  },\n  2: {\n    status: \'processing\',\n    text: \'审批中\'\n  },\n  3: {\n    status: \'success\',\n    text: \'正常\'\n  },\n  4: {\n    status: \'warning\',\n    text: \'已禁用\'\n  }\n}\n</script>\n', NULL, '', '2022-01-20 16:00:06', 'zhanglei001', 9, 'bk_2509711c8b554d98832d1e1a39151df1', '2022-01-20 17:25:31', 'zhanglei001', 'bk_2509711c8b554d98832d1e1a39151df1', NULL, 3, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `templates` VALUES (759, NULL, 627, NULL, 'infoTable.vue.btl', '1', 'src/views/gen/', '1', '', NULL, '', '<template>\n  <a-modal\n    v-model=\"visible\"\n    :destroyOnClose=\"true\"\n    :width=\"900\"\n    :height=\"600\"\n    :visible=\"visible\"\n    :title=\"getTitle(initvalueInfo)\"\n  >\n    <div class=\"main\">\n      <template>\n        <div>\n          <a-row type=\"flex\">\n            <!-- 名称 -->\n            <a-col :span=\"12\" style=\"margin-top: 5px\">\n              <a-col :span=\"6\" align=\"right\">\n                <span>名称:</span>\n              </a-col>\n              <a-col :span=\"18\" style=\"padding-left: 10px;\">\n                <span> {{ initvalueInfo.name }} </span>\n              </a-col>\n            </a-col>\n            <!-- 创建者 -->\n            <a-col :span=\"12\" style=\"margin-top: 5px\">\n              <a-col :span=\"6\" align=\"right\">\n                <span>创建者:</span>\n              </a-col>\n              <a-col :span=\"18\" style=\"padding-left: 10px;\">\n                <span> {{ initvalueInfo.creator }} </span>\n              </a-col>\n            </a-col>\n            <!-- 创建时间 -->\n            <a-col :span=\"12\" style=\"margin-top: 5px\">\n              <a-col :span=\"6\" align=\"right\">\n                <span>创建时间:</span>\n              </a-col>\n              <a-col :span=\"18\" style=\"padding-left: 10px;\">\n                <span> {{ gmtDateFormat(initvalueInfo.gmtCreated) }} </span>\n              </a-col>\n            </a-col>\n            <!-- 更新者 -->\n            <a-col :span=\"12\" style=\"margin-top: 5px\">\n              <a-col :span=\"6\" align=\"right\">\n                <span>更新者:</span>\n              </a-col>\n              <a-col :span=\"18\" style=\"padding-left: 10px;\">\n                <span> {{ initvalueInfo.modifier }} </span>\n              </a-col>\n            </a-col>\n            <!-- 更新时间 -->\n            <a-col :span=\"12\" style=\"margin-top: 5px\">\n              <a-col :span=\"6\" align=\"right\">\n                <span>更新时间:</span>\n              </a-col>\n              <a-col :span=\"18\" style=\"padding-left: 10px;\">\n                <span> {{ gmtDateFormat(initvalueInfo.gmtModified) }} </span>\n              </a-col>\n            </a-col>\n            <!-- 备注 -->\n            <a-col :span=\"24\" style=\"margin-top: 5px\">\n              <a-col :span=\"3\" align=\"right\">\n                <span>备注:</span>\n              </a-col>\n              <a-col :span=\"21\" style=\"padding-left: 10px;\">\n                <span> {{ initvalueInfo.remark }} </span>\n              </a-col>\n            </a-col>\n          <!-- row end -->\n          </a-row>\n        </div>\n      </template>\n    </div>\n    <template slot=\"footer\">\n      <a-button @click=\"handleClose()\"> 返回 </a-button>\n    </template>\n  </a-modal>\n</template>\n<script>\n\nimport moment from \'moment\'\n\nconst formItemLayout = {\n  labelCol: { span: 5 },\n  wrapperCol: { span: 16 }\n}\nexport default {\n  methods: {\n    getTitle (initvalueInfo) {\n      return <span> 详情</span>\n    },\n    show () {\n      this.visible = true\n    },\n    handleClose () {\n      this.visible = false\n    },\n    gmtDateFormat (text) {\n      return moment(text).format(\'yyyy-MM-DD HH:mm:ss\')\n    }\n  },\n  props: {\n    initvalueInfo: {\n      type: Object, // 类型\n      default: null // 默认值\n    }\n  },\n  data () {\n    return {\n      visible: false,\n      formItemLayout\n    }\n  }\n}\n</script>\n', NULL, '', '2022-01-20 16:00:06', 'zhanglei001', 9, 'bk_2509711c8b554d98832d1e1a39151df1', '2022-01-20 16:00:06', 'zhanglei001', 'bk_2509711c8b554d98832d1e1a39151df1', NULL, 3, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `templates` VALUES (760, NULL, 627, NULL, 'addOrEditForm.vue.btl', '1', 'src/views/gen/', '1', '', NULL, '', '<style>\ndiv .line-border{\n  border: 1px #dccece solid;\n  border-style: dashed;\n  padding-top: 20px;\n  padding-right: 20px;\n  margin-top: 15px;\n  margin-bottom: 15px;\n}\n</style>\n<template>\n  <a-modal\n    v-model=\"visible\"\n    :destroyOnClose=\"true\"\n    :width=\"1080\"\n    :visible=\"visible\"\n    :title=\"getTitle(initvalue)\"\n  >\n    <div class=\"main\">\n      <a-form-model ref=\"form\" :layout=\"formLayout\" :model=\"form\" :rules=\"rules\">\n        <a-form-model-item\n          v-show=\"false\"\n          label=\"id\"\n          prop=\"id\"\n          :label-col=\"formItemLayout.labelCol\"\n          :wrapper-col=\"formItemLayout.wrapperCol\"\n        >\n          <a-input v-model=\"form.id\" />\n        </a-form-model-item>\n\n          <!-- 名称  -->\n          <a-form-model-item\n            label=\"名称\"\n            prop=\"name\"\n            :label-col=\"formItemLayout.labelCol\"\n            :wrapper-col=\"formItemLayout.wrapperCol\"\n          >\n            <a-input v-model=\"form.name\" placeholder=\"请输入名称\" />\n          </a-form-model-item>\n      </a-form-model>\n    </div>\n    <template slot=\"footer\">\n      <a-button @click=\"handleEdit()\" type=\"primary\"> 提交 </a-button>\n      <a-button @click=\"handleClose()\"> 取消 </a-button>\n    </template>\n\n  </a-modal>\n</template>\n\n<script>\n\nimport clone from \'clone\'\nimport request from \'@/utils/request\'\nimport { message } from \'ant-design-vue\'\n\nconst formItemLayout = {\n  labelCol: { span: 3 },\n  wrapperCol: { span: 21 }\n}\n\nexport default {\n  components: {\n  },\n  props: {\n    initvalue: {\n      type: Object, // 类型\n      default: null // 默认值\n    }\n  },\n  watch: {\n    initvalue () {\n      if (this.initvalue) {\n        for (const key in this.initvalue) {\n          this.form[key] = this.initvalue[key]\n        }\n      } else {\n        this.form = clone(this.initForm)\n      }\n    },\n    // 清理表单\n    visible (val, newVal) {\n      if (!val) {\n        try {\n          this.form = clone(this.initForm)\n        } catch (e) {}\n      }\n    }\n  },\n  methods: {\n    getTitle (initvalue) {\n      if (initvalue) {\n        return <span> 编辑 </span>\n      } else {\n        return <span> 新建 </span>\n      }\n    },\n    show () {\n      this.visible = true\n    },\n    handleClose () {\n      this.form = clone(this.initForm)\n      this.form.param = \'\'\n      this.visible = false\n    },\n    handleEdit () {\n      this.${dollar}refs.form.validate((bool) => {\n        if (bool) {\n          // 深拷贝,以避开提交时改变源数据\n          var data = JSON.parse(JSON.stringify(this.form))\n          this.commonRequest.head.operationTime = Date.now()\n          this.commonRequest.body = data\n          const commonRequest = this.commonRequest\n          request({\n            url: \'/${entityNameLowerCamelCase}/addOrEdit\',\n            method: \'post\',\n            dataType: \'json\',\n            data: commonRequest\n          }).then(res => {\n            if (res.head.status === \'S\') {\n              message.success(res.head.msg)\n              this.handleClose()\n              this.${dollar}emit(\'refresh\')\n            } else {\n              message.error(res.head.msg)\n            }\n          }).catch((err) => {\n            console.log(err)\n          })\n        }\n      })\n    }\n  },\n  data () {\n    return {\n      visible: false,\n      formLayout: \'horizontal\',\n      formItemLayout,\n      datasourcesList: [],\n      form: {\n<% /** -----------BEGIN 字段循环遍历----------- **/ %>\n<% for(field in table.fields){ %>\n       ${field.propertyName}: null,\n<% } %>\n<% /** -----------END 字段循环遍历----------- **/ %>\n        end: \'\'\n      },\n      initForm: {\n<% /** -----------BEGIN 字段循环遍历----------- **/ %>\n<% for(field in table.fields){ %>\n       ${field.propertyName}: null,\n<% } %>\n<% /** -----------END 字段循环遍历----------- **/ %>\n        end: \'\'\n      },\n      rules: {\n        name: [\n            { required: true, message: \'请输入\' },\n            { max: 50, message: \'最多输入50个字符\' },\n            {\n              pattern: /^(?!(\\s+${dollar}))/,\n              message: \'输入格式错误\'\n            }\n          ]\n        },\n        commonRequest: {\n          head: {\n            operationTime: Date.now(),\n            appId: process.env.VUE_APP_BUUKLE_APP_ID\n          },\n          body: {}\n        },\n        bodyById: {\n          id: 0\n        }\n      }\n  }\n}\n</script>\n', NULL, '', '2022-01-20 16:00:06', 'zhanglei001', 9, 'bk_2509711c8b554d98832d1e1a39151df1', '2022-01-20 17:25:43', 'zhanglei001', 'bk_2509711c8b554d98832d1e1a39151df1', NULL, 3, NULL, NULL, NULL, NULL, NULL);

